var subflow_status_timer = context.get("subflow_status_timer");
const subflow_status_timeout_ms = context.get("subflow_status_timeout_ms");

if (!msg.hasOwnProperty("status")) {
    // First execution, timer must be started
    timer = setTimeout(() =>  {
            node.warn("Connection to OPC UA Server cannot be stablished, check server or configuration parameters");
            msg.status = {fill:"yellow",shape:"dot",text:"Connecting... (see debug)"};
            node.send(msg);
        }, subflow_status_timeout_ms);
    context.set("subflow_status_timer", timer);
    msg = {}
    msg.status = {fill:"yellow", shape:"dot", text:"Connecting..."};
    return msg;
} else {
    // Real status message
    const state = msg.status.text;
    if (state == "active") {
        // Connection done, stop the timer
        if (subflow_status_timer !== null) {
            clearTimeout(subflow_status_timer);
            context.set("subflow_status_timer", null);
        }
        return msg;
    } else if (state.includes("listening")) {
        return msg;
    } else {
        node.warn("Server connection was lost, reconnecting...");
        msg.status = {fill:"yellow",shape:"dot",text:"Reconnecting..."};
        return msg;
    }
}

